mapper.insert:
INSERT INTO board (board_title, board_content, board_category, member_no, board_file, board_orifile) 
VALUES (#{board_title}, #{board_content}, #{board_cate}, (SELECT member_no from member WHERE member_name = #{member_name}), #{board_file}, #{board_orifile}
 )

mapper.select:
UPDATE board SET board_title=#{board_title }, board_content=#{board_content }, board_category=#{board_cate }
WHERE board_no=#{board_no } AND member_no=(SELECT member_no FROM member WHERE member_id=#{member_id})
	OR (board_no=#{board_no } AND ${member_grade}=3)
